  id SERIAL PRIMARY KEY,
  name TEXT,
  img_url TEXT,
  blurb TEXT,
  hashtags TEXT,
  issue TEXT,
  backgroundimg_url TEXT,
  affiliation_id INTEGER REFERENCES affiliation(id)
);

